@Composable
fun DashboardContent(
 modifier: Modifier = Modifier,
 viewModel: HomeViewModel,
 onProjectClick: (String) -> Unit,
 onTaskClick: (String) -> Unit
) {
 val uiState by viewModel.uiState.collectAsState()

 LazyColumn(
 modifier = modifier.fillMaxSize(),
 contentPadding = PaddingValues(16.dp),
 verticalArrangement = Arrangement.spacedBy(16.dp)
 ) {
 // Welcome message
 item {
 Text(
 text = "Welcome back, ${uiState.user?.displayName ?: "User"}!",
 style = MaterialTheme.typography.headlineMedium
 )
 }

 // Project Stats
 item {
 ProjectStatsSection(stats = uiState.projectStats)
 }

 // Recent Projects
 item {
 Text(
 text = "Recent Projects",
 style = MaterialTheme.typography.titleLarge
 )
 }

 if (uiState.recentProjects.isEmpty()) {
 item {
 EmptyStateMessage(
 icon = Icons.Default.Folder,
 message = "No recent projects"
 )
 }
 } else {
 items(uiState.recentProjects) { project ->
 ProjectListItem(
 project = project,
 onClick = { onProjectClick(project.id) }
 )
 }
 }

 // Pending Tasks
 item {
 Text(
 text = "Pending Tasks",
 style = MaterialTheme.typography.titleLarge,
 modifier = Modifier.padding(top = 16.dp)
 )
 }

 if (uiState.pendingTasks.isEmpty()) {
 item {
 EmptyStateMessage(
 icon = Icons.Default.Assignment,
 message = "No pending tasks"
 )
 }
 } else {
 items(uiState.pendingTasks) { task ->
 TaskListItem(
 task = task,
 onClick = { onTaskClick(task.id) }
 )
 }
 }

 // Error message
 uiState.error?.let { error ->
 item {
 Text(
 text = error,
 color = MaterialTheme.colorScheme.error,
 textAlign = TextAlign.Center,
 modifier = Modifier
 .fillMaxWidth()
 .padding(16.dp)
 )
 }
 }
 }

 if (uiState.isLoading) {
 Box(
 modifier = Modifier.fillMaxSize(),
 contentAlignment = Alignment.Center
 ) {
 CircularProgressIndicator()
 }
 }
}

