 const val CHANNEL_ID = "project_manager_channel"
 const val KEY_NOTIFICATION_ID = "notification_id"
 const val KEY_NOTIFICATION_TITLE = "notification_title"
 const val KEY_NOTIFICATION_CONTENT = "notification_content"
 }
