 is Resource.Success -> {
 _uiState.update {
 it.copy(
 isAuthenticated = true,
 user = result.data,
 isLoading = false,
 error = null
 )
 }
 }
 is Resource.Error -> {
 _uiState.update {
 it.copy(
 isLoading = false,
 error = result.message
 )
 }
 }
 is Resource.Loading -> {
 _uiState.update {
 it.copy(isLoading = true, error = null)
 }
 }
 }
 } catch (e: Exception) {
 _uiState.update {
 it.copy(
 isLoading = false,
 error = e.message ?: "Sign in failed"
 )
 }
 }
 }
 }

